pragma solidity >=0.7.0 < 0.9.0;

contract Auction {

    address public highestBidder; // The highest bidder's address 
    address owner;
    uint public highestBid; // The amount of the highest bid 

    function getHighestBidder() public view returns(address){
        return(highestBidder);
    }

    function getHighestBid() public view returns(uint){
        return highestBid;
    }

    mapping(address => uint) public userBalances; // mapping for the amount to return 

    //will be triggered
    event HighestBidIncreased(address bidder, uint amount);

    constructor() public {
    // contractor 
    // 1. Initialize highest bid and the bidder's address
        owner = msg.sender;
        highestBidder = msg.sender;
        highestBid = 0;
    }

    modifier onlyBidder() { // Modifier
        require(isBidder(msg.sender), "You have not made a bid yet.");
        _;
    }



    function isBidder(address addr) public view returns(bool){
        return(userBalances[addr]!=0);
    }

    function bid() public payable{ 
    // Function to process bid
    // 1. Check if the bid is greater than the current highest bid 
    // 2. Update status variable and the amount to return

    //check if bid higher 
        if (msg.value > highestBid) {
            // Saving the amount the previous bidder can withdraw now,
            //since a higher bid has been found
	    if(highestBid == 0){
                userBalances[highestBidder] += highestBid;
	    }
            highestBidder = msg.sender;
            highestBid = msg.value;
            userBalances[msg.sender] += msg.value;
            emit HighestBidIncreased(msg.sender, msg.value);
        }


    } 
    function withdraw() public onlyBidder{ 
    // Function to withdraw the amount of bid to return 
    // 1. Check if the amount to return is greater than zero 
    // 2. Update status variable and return bid 
        uint b = userBalances[msg.sender];
        require(b > 0, "No bid has been made yet");

        //if highest bidder withdraws, it should fail
        require(msg.sender != highestBidder, "Cannot withdraw! You are highestBidder!");


        userBalances[msg.sender] = 0;
        if(!payable(msg.sender).send(b)){  //canot withdraw, revert changes to userBalances
            userBalances[msg.sender] = b;            
        }
    }

    function getContractBalance() public view returns(uint){
        return address(this).balance;
    }

     function getBidderBalance(address _bidder) public view returns(uint){
        return address(_bidder).balance;
    }

    receive() external payable {}
}
